mapper.select:


     SELECT category_id, score_category, user_id, user_image, score_totalPoint,  RANK() OVER(ORDER BY score_totalPoint DESC) AS score_rank
	  FROM(
      		SELECT  a.category_id, a.user_id, a.score_category , u.user_image, a.score_totalPoint
	  		FROM  user_table u,(
                 				SELECT category_id, score_category, user_id, sum(score_point) AS score_totalPoint
                  				FROM score s
                  				GROUP BY category_id, user_id, score_category
                                HAVING score_category = #{score_category}
                  				) a
      WHERE u.user_id = a.user_id
      AND category_id = #{category_id}
      )



		SELECT user_id, score_point, score_content, score_category, score_date
		FROM   (
        	    SELECT  user_id, score_point, score_content, score_category, score_date
         	   	FROM	score
          	  	WHERE	user_id = #{user_id}
          	  	AND		category_id = #{category_id}
           		)
		WHERE score_category = 1
		ORDER BY score_date DESC



		SELECT user_id, score_point, score_content, score_category, score_date
		FROM   (
        	    SELECT  user_id, score_point, score_content, score_category, score_date
         	    FROM	score
          	    WHERE	user_id = #{user_id}
          	    AND		category_id = #{category_id}
           		)
		WHERE score_category = 2
		ORDER BY score_date DESC



SELECT s.category_id, u.user_id, u.user_image, (
        	     				 SELECT  SUM(score_point) AS score_totalPoint
           		 				 FROM  score
                				  WHERE user_id = u.user_id
             	 				 GROUP BY user_id
                 				 ) as score_totalPoint
FROM    user_table u, score s
WHERE   u.user_id = s.user_id
AND     u.user_id = #{user_id}
AND		s.category_id = #{category_id}



SELECT	user_id, user_email, user_image ,user_signdate
FROM	user_table
WHERE	user_id= #{user_id}
